# Bubble sort implementation
import sys

def bubble_sort(arr):
    n = len(arr)
    for i in range(n):
        for j in range(0, n-i-1):
            if arr[j] > arr[j+1]:
                arr[j], arr[j+1] = arr[j+1], arr[j]
    return arr

if __name__ == "__main__":
    data = list(map(int, sys.stdin.read().strip().split()))
    sorted_data = bubble_sort(data)
    print(" ".join(map(str, sorted_data)))
